package com.education.common.utils;import java.util.ArrayList;import java.util.List;import java.util.Map;/** * @author zengjintao * @create 2019/7/12 20:12 * @since 1.0 **/public class MapTreeUtils {    /**     * 获取子类 的所有父类结合     * @param dataList     * @param parentId     * @return     */    public static List<Map> getParentList(List<Map> dataList, int parentId) {        List<Map> parentList = new ArrayList<>();        return getParent(dataList, parentId, parentList);    }    private static List<Map> getParent(List<Map> dataList, int parentId, List<Map> parentList) {        for (Map data : dataList) {            Integer id = (Integer) data.get("value");            if (ObjectUtils.isEmpty(id)) {                id = (Integer) data.get("id");            }            Integer itemParentId = (Integer) data.get("parent_id");            if (parentId == id) {                parentList.add(data);                if (itemParentId != ResultCode.FAIL) {                    return getParent(dataList, itemParentId, parentList);                }            }        }        return parentList;    }    public static List<Map> buildTreeData(List<Map> dataList) {        List<Map> result = new ArrayList<>();        List<Map> parentList = getRoot(dataList);        for (Map tree : parentList) {            setChildren(tree, dataList);            result.add(tree);        }        return result;    }    public static List<Map> getChildrenTree(List<Map> dataList, int parentId) {        List<Map> childrenTree = new ArrayList();        dataList.forEach(data -> {            if ((Integer)data.get("parent_id") == parentId) {                childrenTree.add(data);            }        });        childrenTree.forEach(data -> {            setChildren(data, dataList);        });        return childrenTree;    }    private static void setChildren(Map data, List<Map> dataList) {        Integer id = (Integer) data.get("value");        if (ObjectUtils.isEmpty(id)) {            id = (Integer) data.get("id");        }        List<Map> children = getChildrenTree(dataList, id);        if (ObjectUtils.isNotEmpty(children)) {            data.put("children", children);        }    }    /**     * 获取顶级父类数据     * @param dataList     * @return     */    private static List<Map> getRoot(List<Map> dataList) {        List<Map> rootList = new ArrayList<>();        dataList.forEach(root -> {            if ((Integer)root.get("parent_id") == 0) {                rootList.add(root);            }        });        return rootList;    }}